package com.chen.app;

import sun.misc.BASE64Decoder;

import java.io.FileOutputStream;
import java.io.OutputStream;
import java.text.DecimalFormat;

public class Base64Img {
    public static void main(String[] args) {
        String imagebase64 = "data:image/png;base64,";
        string2Image(imagebase64,"1.png");
//        System.out.println(showSize(imagebase64));
    }

    public static String showSize(String img){
        String replace = img.replace("data:image/png;base64,", "");
        int i = replace.indexOf("=");

        if (replace.indexOf("=")>0){
            replace = replace.substring(0,i);
        }

        int length = replace.length();
        int fileLength = (length - (length / 8) * 2);

        DecimalFormat df = new DecimalFormat("#.00");
        String format = df.format(fileLength / 1024);

        int i1 = format.indexOf(".");
        String substring = format.substring(i1 + 1, 2);

        if (substring == "00"){
            return format.substring(0,i1) + format.substring(i1 + 3,2);
        }
        return format;
    }

    public static boolean string2Image(String imgStr, String imgFilePath) {
        // 对字节数组字符串进行Base64解码并生成图片
        if (imgStr == null)
            return false;
        try {
            // Base64解码
            byte[] b = new BASE64Decoder().decodeBuffer(imgStr);
            for (int i = 0; i < b.length; ++i) {
                if (b[i] < 0) {
                    // 调整异常数据
                    b[i] += 256;
                }
            }
            // 生成Jpeg图片
            OutputStream out = new FileOutputStream(imgFilePath);
            out.write(b);
            out.flush();
            out.close();
            return true;
        } catch (Exception e) {
            return false;
        }
    }

}
